import { Fragment } from 'react';
import { useParams, Route , Link} from 'react-router-dom';

import Comments from '../components/comments/Comments';

import HighlightedQuotes from '../components/quotes/HighlightedQuote'

const DUMMY_QUOTES = [
  { id: 'q1', author: 'Max', text: 'Learning React is fun!' },
  { id: 'q2', author: 'Maximilian', text: 'Learning React is great!' },
];
const QuoteDetail = () => {
  const params = useParams();
  //useParams is used in URL.
  const quote = DUMMY_QUOTES.find((quote) => quote.id === params.quoteId)

  if(!quote){
    <p>No Quote Found</p>
  }
  return ( 
    <Fragment>


    <HighlightedQuotes text={quote.text} author={quote.author} />

    {/*  using nested router to condtionally render URl */}
    <Route path={`/quotes/${params.quoteId}`} exact>
        <div className="centered">
        <Link to={`/quotes/${params.quoteId}/comments`}>
          load comments
    </Link>
        </div>
    </Route>



      <Route path={`/quotes/${params.quoteId}/comments`}>
        <Comments />
      </Route>



    </Fragment>
  );
};

export default QuoteDetail;
