import { Fragment } from 'react';

import QuoteItem from './QuoteItem';
import classes from './QuoteList.module.css';
import {useHistory, useLocation} from 'react-router-dom'
const QuoteList = (props) => {


  const sortQuotes = (quotes, ascending) => {
    return quotes.sort((quoteA, quoteB) => {
      if (ascending) {
        return quoteA.id > quoteB.id ? 1 : -1;
      } else {
        return quoteA.id < quoteB.id ? 1 : -1;
      }
    });
  };

const history = useHistory()
const location = useLocation()

  const queryParams = new URLSearchParams(location.search)


  // a boolean (true or false depending if asc is false)which is used in ternary operator in JSX below 
  const isSortingAscending = queryParams.get('sort') === 'asc'

  const changeSortingHandler = () => {
    // key and value pair for queryParams  to use in URL 

      // changes url value depending on order 
    history.push('/quotes?sort=' + (isSortingAscending ? 'dec' : 'asc'));

  }

  return (
    <Fragment>
    <div className={classes.sorting}>
      <button onClick={changeSortingHandler} >{isSortingAscending ? 'Descending' : 'Ascending'}</button>
    </div>
      <ul className={classes.list}>
        {sortQuotes.map((quote) => (
          <QuoteItem
            key={quote.id}
            id={quote.id}
            author={quote.author}
            text={quote.text}
          />
        ))}
      </ul>
    </Fragment>
  );
};

export default QuoteList;
